pv readings.csv | grep 'rainfall' | grep -v '|\|undefined\|NaN\|dateTime,date,measure' | csvcut --columns=1,6,13 | psql -c "COPY temptimecsv(timestamp, stationref, value) FROM stdin DELIMITER ',' CSV;"
